BEGIN TRANSACTION create2;
Drop TABLE IF EXISTS HospitalGeo.info;
create TABLE HospitalGeo.info(
    H_ID int NOT NULL,
    H_Name varchar(50) NOT NULL,
    H_Suspended int NOT NULL,
    H_Available int,
    H_Requiring int,
    H_Latitude float NOT NULL,
    H_Longitude float NOT NULL,
    PRIMARY KEY (H_ID)
);
COMMIT;

